import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import Candle from './Candle'
import './App.css'

class App extends Component{
  constructor(props) {
    super(props);
    this.state = {
      option: [
        {id: 0, name:"Fresh Balsalm", cost:"9", wicks:"1", season:"winter", burntime:"2"},
        {id: 1, name:"Flannel", cost:"14", wicks:"3", season:"winter", burntime:"4"},
        {id: 2, name:"Frosted Cranberry", cost:"24", wicks:"3", season:"winter", burntime:"5"},
        {id: 3, name:"Leaves", cost:"12", wicks:"1", season:"fall", burntime:"3"},
        {id: 4, name:"Pumpkin", cost:"14", wicks:"3", season:"fall", burntime:"4"},
        {id: 5, name:"Sweater Weather", cost:"24", wicks:"3", season:"fall", burntime:"5"},
        {id: 6, name:"Cactus Blossom", cost:"9", wicks:"1", season:"spring", burntime:"2"},
        {id: 7, name:"Peach Bellini", cost:"14", wicks:"3", season:"spring", burntime:"4"},
        {id: 8, name:"The Perfect Spring", cost:"24", wicks:"3", season:"spring", burntime:"5"},
        {id: 9, name:"Island Waters", cost:"12", wicks:"1", season:"summer", burntime:"3"},
        {id: 10, name:"Boathouse Row", cost:"14", wicks:"3", season:"summer", burntime:"4"},
        {id: 11, name:"Watermelon Lemonade", cost:"24", wicks:"3", season:"summer", burntime:"5"}
      ],
      cartCandles: [],
      cartTotal: 0
    }
  }

  updateTotal(cost){
    const newTotal = this.state.totalCost + cost;
    this.setState({
      cartTotal: newTotal
    });
  }
  render(){
    return (
      <div className="App">

      </div>
    );
  }

}


export default App;
