import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav'
import DisplayList from './DisplayList'
import ShoppingCart from './ShoppingCart'
import NavDropdown from 'react-bootstrap/NavDropdown'
import 'bootstrap/dist/css/bootstrap.min.css'
import './FilteredList.css'


class FilteredList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      season: "All",
      size: "Al",
      sort: "none",
      cartTotal: 0,
      cartContents: {}
    }
    this.addToCart=this.addToCart.bind(this);
    this.removeOne=this.removeOne.bind(this);
    this.removeAll=this.removeAll.bind(this);
    this.onSortByPrice=this.onSortByPrice.bind(this);
    this.onSelectFilterSize=this.onSelectFilterSize.bind(this);
    this.onSelectFilterSeason=this.onSelectFilterSeason.bind(this);
    this.matchesFilters=this.matchesFilters.bind(this);
    this.sortOptions=this.sortOptions.bind(this);
  }

  // call in cart AND in display list
  addToCart(object) {
    let newContents = this.state.cartContents;
    if(object.id in newContents){
      newContents[object.id]["quantity"]++;
    } else {
      let value = {quantity: 1, name: object.name, cost: object.cost, id: object.id, pic:object.pic, size:object.size, burntime:object.burntime}
      newContents[object.id]=value
    }
    this.setState({cartContents: newContents});
    this.calculateTotal();
  }

  // only called from cart
  removeOne(object){
    let newContents = this.state.cartContents;
    newContents[object.id]["quantity"]--;
    if (newContents[object.id]["quantity"]===0){
      delete newContents[object.id];
    }
    this.setState({cartContents:newContents});
    this.calculateTotal();
  }

  removeAll(object){
    let newContents = this.state.cartContents;
    delete newContents[object.id];
    this.setState({cartContents: newContents});
    this.calculateTotal();
  }

  calculateTotal(){
    // for every item in the cart dictionary, multiply price by quantity and add it all together
    let totalCost = 0;
    for (var key in this.state.cartContents){
      let cost = this.state.cartContents[key].quantity*this.state.cartContents[key].cost;
      totalCost = totalCost+ cost;
    }
    this.setState({cartTotal:totalCost});
  }

  onSortByPrice = event =>{
    this.setState({
      sort: event
    });
  }

  onSelectFilterSeason = event => {
    this.setState({
      season: event
    })
  }

  onSelectFilterSize = event => {
    this.setState({
      size: event
    })
  }

  matchesFilters = item => {
    if(this.state.season==="All"&&this.state.size==="Al"){
      return true;
    } else if(this.state.season==="All"&&this.state.size===item.size){
      return true;
    } else if(this.state.season===item.season&&this.state.size==="Al"){
      return true;
    } else if(this.state.season===item.season&&this.state.size===item.size){
      return true;
    } else {
      return false;
    }
  }

  sortOptions(options) {
    if(this.state.sort==="lowhigh"){
      options.sort((a, b)=> a.cost-b.cost);
    } else if(this.state.sort==="highlow"){
      options.sort((a,b)=>b.cost-a.cost);
    }
    return options;
  }

  render() {
    return (
      <div className="filtered">
        <Navbar bg="light" expand="xl" className="Navbar">
          <Navbar.Brand className="brand">DISCOUNT CANDLE</Navbar.Brand>
          <NavDropdown title="Filter by Size" id="collasible-nav-dropdown">
            <Nav.Item><Nav.Link eventKey="Al" onSelect={this.onSelectFilterSize}>All</Nav.Link></Nav.Item>
            <Nav.Item><Nav.Link eventKey="3" onSelect={this.onSelectFilterSize}>3-wick</Nav.Link></Nav.Item>
            <Nav.Item><Nav.Link eventKey="1" onSelect={this.onSelectFilterSize}>1-wick</Nav.Link></Nav.Item>
          </NavDropdown>
          <NavDropdown title="Filter by Season" id="collasible-nav-dropdown">
            <Nav.Item><Nav.Link eventKey="All" onSelect={this.onSelectFilterSeason}>All</Nav.Link></Nav.Item>
              <Nav.Item><Nav.Link eventKey="Winter" onSelect={this.onSelectFilterSeason}>Winter</Nav.Link></Nav.Item>
              <Nav.Item><Nav.Link eventKey="Summer" onSelect={this.onSelectFilterSeason}>Summer</Nav.Link></Nav.Item>
              <Nav.Item><Nav.Link eventKey="Spring" onSelect={this.onSelectFilterSeason}>Spring</Nav.Link></Nav.Item>
              <Nav.Item><Nav.Link eventKey="Fall" onSelect={this.onSelectFilterSeason}>Fall</Nav.Link></Nav.Item>
          </NavDropdown>
          <NavDropdown title="Sort by Price" id="collasible-nav-dropdown">
            <NavDropdown.Item eventKey="none"  onSelect={this.onSortByPrice}>Select</NavDropdown.Item>
            <NavDropdown.Item eventKey="lowhigh"  onSelect={this.onSortByPrice}>Low-High</NavDropdown.Item>
            <NavDropdown.Item eventKey="highlow"  onSelect={this.onSortByPrice}>High-Low</NavDropdown.Item>
          </NavDropdown>
        </Navbar>
        <div className="row">
          <DisplayList  list={this.sortOptions(this.props.list.filter(this.matchesFilters))}
                        key={0}
                        addToCart={this.addToCart}/>
          <ShoppingCart contents={this.state.cartContents}
                        key={1}
                        addToCart={this.addToCart}
                        removeOne={this.removeOne}
                        removeAll={this.removeAll}
                        cartTotal={this.state.cartTotal}/>
        </div>
      </div>
    )
  }

}

export default FilteredList;
