import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav'
import Button from 'react-bootstrap/Button'
import './ShoppingCart.css'
import InputGroup from 'react-bootstrap/InputGroup'
import InputGroupAddon from 'react-bootstrap/InputGroup'
import Input from 'react-bootstrap/InputGroup'


class ShoppingCart extends React.Component {

  constructor(props) {
    super(props);
  }
  // each of these items will have an add to cart button
  // we will filter which ones are in the cart
  render() {
    return (
      <div className="wrapper">
        <h1>SHOPPING CART</h1>
        <h1>TOTAL COST: ${this.props.cartTotal}</h1>
        <div className="cards">
          {
            Object.keys(this.props.contents).map(
              (key) =>
              <div className="flex-row">
                <img id="img" src={this.props.contents[key].pic} alt="Card image cap"></img>
                <div className="flex-column">
                  <h3>{this.props.contents[key].name}</h3>
                  <div className="card-body">
                    <div>Quantity: {this.props.contents[key].quantity} x ${this.props.contents[key].cost} each</div>
                  </div>
                </div>
                <div className="flex-row">
                  <Button variant="outline-dark" onClick={()=>this.props.addToCart(this.props.contents[key])} block>Add 1</Button>
                  <Button variant="outline-dark" onClick={()=>this.props.removeOne(this.props.contents[key])} block>Remove 1</Button>
                  <Button variant="danger" onClick={()=>this.props.removeAll(this.props.contents[key])} block>Remove All</Button>
                </div>
              </div>
            )
          }
        </div>
      </div>
    )
  }

}

export default ShoppingCart;
