import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav'
import DisplayList from './DisplayList'
import ShoppingCart from './ShoppingCart'
import NavDropdown from 'react-bootstrap/NavDropdown'
class FilteredList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      season: "all",
      size: "al",
      sort: "none",
      cartTotal: 0,
      cartContents: {}
    }
  }

  // call in cart AND in display list
  addToCart(object) {
    let newContents = this.state.cartContents;
    // if there's already one in the cart, just adjust the quantity
    if(object.id in this.state.cartContents){
      newContents[object.id]["quantity"]++;
    } else {
      // create the object
      // add it to new contents
      let value = {quantity: 1, name: object.name, cost:object.cost, id: object.id}
      newContents.push({key: object.id, value:value});
    }
    // set state to use newcontents for cartcontents
    this.setState({cartContents:newContents});
    this.calculateTotal();
  }

  // only called from cart
  removeOne(object){
    let newContents = this.state.cartContents;
    newContents[object.id]["quantity"]--;

  }

  calculateTotal(){
    // for every item in the cart dictionary, multiply price by quantity and add it all together
    let totalCost = 0;
    for (let object in this.state.cartContents){
      totalCost = totalCost+ (object["quantity"]*object["cost"]);
    }
    this.setState({cartTotal:totalCost});
  }

  onSortByPrice = event =>{
    this.setState({
      sort: event
    });
  }

  onSelectFilterSeason = event => {
    this.setState({
      season: event
    })
  }

  onSelectFilterSize = event => {
    this.setState({
      size: event
    })
  }

  matchesFilters = item => {
    if(this.state.season==="all"&&this.state.size==="al"){
      return true;
    } else if(this.state.season==="all"&&this.state.size===item.size){
      return true;
    } else if(this.state.season===item.season&&this.state.size==="al"){
      return true;
    } else if(this.state.season===item.season&&this.state.size===item.size){
      return true;
    } else {
      return false;
    }
  }

  sortOptions(options) {
    if(this.state.sort==="lowhigh"){
      options.sort((a, b)=> a.cost-b.cost);
    } else if(this.state.sort==="highlow"){
      options.sort((a,b)=>b.cost-a.cost);
    }
    return options;
  }

  render() {
    return (
      <div>
        <Navbar bg="light" expand="lg">
          <Nav.Item><Nav.Link eventKey="al" onSelect={this.onSelectFilterSize}>All</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="3" onSelect={this.onSelectFilterSize}>3-wick</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="1" onSelect={this.onSelectFilterSize}>1-wick</Nav.Link></Nav.Item>
        </Navbar>
        <Navbar bg="light" expand="lg">
          <Nav.Item><Nav.Link eventKey="winter" onSelect={this.onSelectFilterSeason}>Winter</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="summer" onSelect={this.onSelectFilterSeason}>Summer</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="spring" onSelect={this.onSelectFilterSeason}>Spring</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="fall" onSelect={this.onSelectFilterSeason}>Fall</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="all" onSelect={this.onSelectFilterSeason}>All</Nav.Link></Nav.Item>
        </Navbar>
        <NavDropdown title="Dropdown" id="collasible-nav-dropdown">
          <NavDropdown.Item eventKey="none"  onSelect={this.onSortByPrice}>Select</NavDropdown.Item>
          <NavDropdown.Item eventKey="lowhigh"  onSelect={this.onSortByPrice}>Low-High</NavDropdown.Item>
          <NavDropdown.Item eventKey="highlow"  onSelect={this.onSortByPrice}>High-Low</NavDropdown.Item>
        </NavDropdown>
        <DisplayList list={this.sortOptions(this.props.list.filter(this.matchesFilters))}/>
      </div>
    )
  }

}

export default FilteredList;
