import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav'
import Button from 'react-bootstrap/Button'
import './ShoppingCart.css'
import InputGroup from 'react-bootstrap/InputGroup'
import InputGroupAddon from 'react-bootstrap/InputGroup'
import Input from 'react-bootstrap/InputGroup'
import FormControl from 'react-bootstrap/FormControl'




class ShoppingCart extends React.Component {

  constructor(props) {
    super(props);
  }
  // each of these items will have an add to cart button
  // we will filter which ones are in the cart
  render() {
    return (
      <div>
        <h1>TOTAL COST: ${this.props.cartTotal}</h1>
        {
          Object.keys(this.props.contents).map(
            (key) =>
            <div className="card" id="card">
              <img className="card-img-top" src={this.props.contents[key].pic} alt="Card image cap"></img>
              <h3>${this.props.contents[key].cost}</h3>
              <h3>{this.props.contents[key].name}</h3>
              <div className="card-body">
                <div>Quantity: {this.props.contents[key].quantity}</div>
              </div>
              <InputGroup className="def-number-input number-input">
      					<InputGroup.Prepend>
      						<Button className="equal" variant="outline-secondary">-</Button>
      					</InputGroup.Prepend>
      					<FormControl className="equal" aria-describedby="inputGroup-sizing-sm" />
      					<InputGroup.Append>
      						<Button className="equal" variant="outline-secondary">+</Button>
      					</InputGroup.Append>
      				</InputGroup>
              <div className="btn-group" role="group" aria-label="Basic example" variant="info" size="lg" block>
                <button type="button" className="btn btn-outline-dark" onClick={()=>this.props.removeOne(this.props.contents[key])}>-</button>
                <button type="button" className="btn btn-outline-dark" onClick={()=>this.props.addToCart(this.props.contents[key])}>+</button>
              </div>
              <button type="button" className="btn btn-danger" onClick={()=>this.props.removeAll(this.props.contents[key])}>Remove All</button>
            </div>
          )
        }
      </div>
    )
  }

}

export default ShoppingCart;
