import React, {Component} from 'react';
import ReactDOM from 'react-dom';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav'

class FilteredList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      season: "all",
      wicks: "all"
    }
  }

  onSelectFilterSeason = event => {
    this.setState({
      season: event
    })
  }

  onSelectFilterSize = event => {
    this.setState({
      wicks: event
    })
  }

  matchesFilterSize = item => {
  	// all items should be shown when no filter is selected
  	if(this.state.wicks === "all") {
  		return true
  	} else if (this.state.wicks === item.wicks) {
  		return true
  	} else {
  		return false
  	}
  }

  matchesFilterSeason = item => {
    if(this.state.season === "all") {
      return true
    } else if (this.state.wicks === item.wicks) {
      return true
    } else {
      return false
    }
  }




  render() {
    return (
      <div>
        <Navbar bg="light" expand="lg">
          <Nav.Item><Nav.Link eventKey="all" onSelect={this.onSelectFilterSize}>All</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="three" onSelect={this.onSelectFilterSize}>3-wick</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="one" onSelect={this.onSelectFilterSize}>1-wick</Nav.Link></Nav.Item>
        </Navbar>
        <Navbar bg="light" expand="lg">
          <Nav.Item><Nav.Link eventKey="winter" onSelect={this.onSelectFilterSeason}>Winter</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="summer" onSelect={this.onSelectFilterSeason}>Summer</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="spring" onSelect={this.onSelectFilterSeason}>Spring</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="fall" onSelect={this.onSelectFilterSeason}>Fall</Nav.Link></Nav.Item>
          <Nav.Item><Nav.Link eventKey="all" onSelect={this.onSelectFilterSeason}>All</Nav.Link></Nav.Item>
        </Navbar>
      </div>
    )
  }

}

export default FilteredList;
